mapper.select:

WHERE

    UserId = #{condition.userId}


    and ItemId = #{condition.itemId}



        and ( OrderTime >= #{condition.startTime} OR TransTime >= #{condition.startTime})


        and OrderTime >= #{condition.startTime}


        and TransTime >= #{condition.startTime}




        and ( OrderTime < #{condition.endTime} OR TransTime < #{condition.endTime})


        and OrderTime < #{condition.endTime}


        and TransTime < #{condition.endTime}



    and ProductStatus = #{condition.productStatus}

ORDER BY OrderTime DESC
